import Input from './components/Input';
import Buttom from './components/Buttom';

import { Container, Content, Row } from './styles';
import { useState } from 'react';

const App = () => {
  const [currentNumber, setCurrentNumber] = useState('0');

  const handleAddnumber = (number) => {
    setCurrentNumber(prev => `${number}${prev}`);
  }
  return (
    <Container>
      <Content>
        <Input value={currentNumber}/>
        <Row>
          <Buttom label="x" onClick={() => handleAddnumber('x')}/>
          <Buttom label="%" onClick={() => handleAddnumber('%')}/>
          <Buttom label="C" onClick={() => handleAddnumber('C')}/>
          <Buttom label="X" onClick={() => handleAddnumber('X')}/>
        </Row>
        <Row>
          <Buttom label="7" onClick={() => handleAddnumber('7')}/>
          <Buttom label="8" onClick={() => handleAddnumber('8')}/>
          <Buttom label="9" onClick={() => handleAddnumber('9')}/>
          <Buttom label="-" onClick={() => handleAddnumber('-')}/>
        </Row>
        <Row>
          <Buttom label="4" onClick={() => handleAddnumber('4')}/>
          <Buttom label="5" onClick={() => handleAddnumber('5')}/>
          <Buttom label="6" onClick={() => handleAddnumber('6')}/>
          <Buttom label="+" onClick={() => handleAddnumber('+')}/>
        </Row>
        <Row>
          <Buttom label="1" onClick={() => handleAddnumber('')}/>
          <Buttom label="2" onClick={() => handleAddnumber('')}/>
          <Buttom label="3" onClick={() => handleAddnumber('')}/>
          <Buttom label="=" onClick={() => handleAddnumber('')}/>
        </Row>
      </Content>
    </Container>
  );
}

export default App;
