import Input from './components/Input';
import Buttom from './components/Buttom';

import { Container, Content, Row } from './styles';
import { useState } from 'react';

const App = () => {
  const [currentNumber, setCurrentNumber] = useState('0');

  const handleAddnumber = () => {
    setCurrentNumber(prev => prev + 1);
  }
  return (
    <Container>
      <Content>
        <Input value={currentNumber}/>
        <Row>
          <Buttom label="x"/>
          <Buttom label="%"/>
          <Buttom label="C"/>
          <Buttom label="X"/>
        </Row>
        <Row>
          <Buttom label="7"/>
          <Buttom label="8"/>
          <Buttom label="9"/>
          <Buttom label="-"/>
        </Row>
        <Row>
          <Buttom label="4"/>
          <Buttom label="5"/>
          <Buttom label="6"/>
          <Buttom label="+"/>
        </Row>
        <Row>
          <Buttom label="1"/>
          <Buttom label="2"/>
          <Buttom label="3"/>
          <Buttom label="="/>
        </Row>
      </Content>
    </Container>
  );
}

export default App;
